// Funci√≥n para normalizar las fechas en formato ISO
function normalizeDate(date) {
  if (typeof date === 'string') {
    return date.split('T')[0];
  }
  return date.toISOString().split('T')[0];
}

// SOLUCI√ìN FINAL: Procesar eventos con manejo correcto de zona horaria
function processEvents() {
  // Mapa de bloques de tiempo ocupados por d√≠a
  const occupiedBlocks = {};

  console.log("\n===== PROCESANDO EVENTOS CON AJUSTE DE ZONA HORARIA =====");

  // Procesar cada evento para identificar las horas ocupadas
  for (const event of allEvents) {
    try {
      // Extraer fecha y hora
      const startStr = event.start.dateTime || event.start.date;
      const endStr = event.end.dateTime || event.end.date;

      // Crear objetos de fecha
      const startDate = new Date(startStr);
      const endDate = new Date(endStr);

      if (isNaN(startDate.getTime()) || isNaN(endDate.getTime())) {
        console.log(`‚ùå Fechas inv√°lidas para evento: ${event.summary || 'Sin t√≠tulo'}`);
        continue;
      }

      // Obtener d√≠a en formato YYYY-MM-DD
      const dateKey = normalizeDate(startDate);

      // CORRECCI√ìN CLAVE: Extraer horas directamente de la cadena para evitar problemas de zona horaria
      // Buscar los componentes de hora y minuto en la cadena original
      function extractTimeComponents(dateString) {
        // Para eventos con formato ISO completo: "2025-03-21T09:00:00+01:00"
        const timeMatch = dateString.match(/T(\d{2}):(\d{2}):(\d{2})/);
        if (timeMatch) {
          return {
            hour: parseInt(timeMatch[1], 10),
            minute: parseInt(timeMatch[2], 10),
            second: parseInt(timeMatch[3], 10)
          };
        }
        return null;
      }

      // Extraer hora de inicio y fin
      const startTime = extractTimeComponents(startStr);
      const endTime = extractTimeComponents(endStr);

      if (!startTime || !endTime) {
        console.log(`‚ö†Ô∏è No se pudo extraer la hora para: ${event.summary || 'Sin t√≠tulo'}`);
        continue;
      }

      // Inicializar estructura para este d√≠a si no existe
      if (!occupiedBlocks[dateKey]) {
        occupiedBlocks[dateKey] = [];
      }

      // Obtener las horas directamente, sin reinterpretaci√≥n de zona horaria
      const startHour = startTime.hour;
      const endHour = endTime.hour;
      const endMinute = endTime.minute;

      // Ajustar fin para que si termina exactamente en hora, no bloquear esa hora
      const adjustedEndHour = (endMinute === 0 && endTime.second === 0) ? endHour : endHour + 1;

      // Agregar el bloque ocupado
      occupiedBlocks[dateKey].push({
        summary: event.summary || "Sin t√≠tulo",
        startHour: startHour,
        endHour: adjustedEndHour,
        startTime: startTime,
        endTime: endTime
      });

      console.log(`Evento "${event.summary || 'Sin t√≠tulo'}" (${dateKey}): ${startHour}:00 - ${adjustedEndHour}:00`);
    } catch (error) {
      console.error(`Error procesando evento: ${error.message}`);
    }
  }

  return occupiedBlocks;
