// Funciones para formatear a texto natural
function numberToText(num) {
  const units = ['', 'uno', 'dos', 'tres', 'cuatro', 'cinco', 'seis', 'siete', 'ocho', 'nueve', 'diez', 
                'once', 'doce', 'trece', 'catorce', 'quince', 'diecis√©is', 'diecisiete', 'dieciocho', 'diecinueve', 'veinte', 
                'veintiuno', 'veintid√≥s', 'veintitr√©s', 'veinticuatro', 'veinticinco', 'veintis√©is', 'veintisiete', 'veintiocho', 'veintinueve', 'treinta', 'treinta y uno'];

  if (num <= 31) {
    return units[num];
  }

  if (num >= 2000 && num <= 2099) {
    const thousand = Math.floor(num / 1000);
    const remainder = num % 1000;

    if (remainder === 0) {
      return 'dos mil';
    }

    if (remainder < 100) {
      return `dos mil ${numberToText(remainder)}`;
    }

    return `dos mil ${numberToText(remainder)}`;
  }

  return num.toString();
}

function getHourText(hour) {
  if (hour === 0) return "doce de la noche";
  if (hour === 12) return "doce del mediod√≠a";
  if (hour === 13) return "una de la tarde";
  if (hour === 14) return "dos de la tarde";
  if (hour === 15) return "tres de la tarde";
  if (hour === 16) return "cuatro de la tarde";
  if (hour === 17) return "cinco de la tarde";
  if (hour === 18) return "seis de la tarde";
  if (hour === 19) return "siete de la tarde";
  if (hour === 20) return "ocho de la tarde";
  if (hour === 21) return "nueve de la noche";
  if (hour === 22) return "diez de la noche";
  if (hour === 23) return "once de la noche";

  return `${['una', 'dos', 'tres', 'cuatro', 'cinco', 'seis', 'siete', 'ocho', 'nueve', 'diez', 'once'][hour-1]} de la ma√±ana`;
}

function getDateText(date) {
  const day = numberToText(date.getDate());
  const monthNames = [
    "enero", "febrero", "marzo", "abril", "mayo", "junio",
    "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"
  ];
  const month = monthNames[date.getMonth()];
  const year = numberToText(date.getFullYear());

  const weekdayNames = ["domingo", "lunes", "martes", "mi√©rcoles", "jueves", "viernes", "s√°bado"];
  const weekday = weekdayNames[date.getDay()];

  return `${weekday} ${day} de ${month} de ${year}`;
}

function formatSlotsAsNaturalText(slots) {
  if (slots.length === 0) {
    return "No hay slots disponibles para el per√≠odo solicitado.";
  }

  const slotsByDate = {};
  slots.forEach(slot => {
    // Para texto natural, convertir de UTC a hora local (Madrid)
    const slotDate = new Date(slot.start);

    // Ajustar a zona horaria de Madrid para el texto
    const localDate = new Date(slotDate);
    localDate.setHours(slotDate.getUTCHours() + timezoneDiff);

    const dateKey = normalizeDate(localDate);

    if (!slotsByDate[dateKey]) {
      slotsByDate[dateKey] = [];
    }

    slotsByDate[dateKey].push(localDate);
  });

  let result = "";
  for (const dateKey in slotsByDate) {
    const dateSlots = slotsByDate[dateKey];
    const date = new Date(dateKey);

    result += `Para el ${getDateText(date)} tenemos disponibles: `;

    const slotTexts = dateSlots.map(slotDate => {
      const startHour = slotDate.getHours();
      const endHour = (startHour + 1) % 24;
      const startText = getHourText(startHour).replace(" de la ma√±ana", "").replace(" de la tarde", "").replace(" de la noche", "").replace(" del mediod√≠a", "");
      const endText = getHourText(endHour);

      return `de ${startText} a ${endText}`;
    });

    if (slotTexts.length === 1) {
      result += slotTexts[0];
    } else if (slotTexts.length === 2) {
      result += `${slotTexts[0]} y ${slotTexts[1]}`;
    } else {
      const lastSlot = slotTexts.pop();
      result += `${slotTexts.join(", ")} y ${lastSlot}`;
    }

    result += ". ";
  }

  return result.trim();
}

