// Configuraci√≥n de horarios de trabajo
const HORARIO_LABORAL = {
  inicio: 9, // 9:00 AM
  fin: 18,  // 6:00 PM
  duracionCita: 60, // minutos (1 hora)
  diasHabiles: [1, 2, 3, 4, 5] // Lunes a Viernes (0=Domingo, 6=S√°bado)
};

// Configuraci√≥n de margen de tiempo y l√≠mites
const MARGEN_HORAS = 4; // Margen de 4 horas para citas m√°s inmediatas
const MAX_SLOTS_MOSTRAR = 3; // N√∫mero m√°ximo de slots a mostrar
const LIMITE_MESES = 2; // L√≠mite de 2 meses para consultas futuras

// Obtener eventos del calendario
// El input para Code Tools viene como una cadena
let datos;
try {
  // Intentar parsear la entrada como JSON
  datos = JSON.parse($input.query);
} catch (e) {
  // Si no es JSON v√°lido, usar el texto como est√°
  datos = $input.query;
}

// Analizar si hay fecha y hora espec√≠ficas solicitadas
let fechaEspecifica = null;
let horaEspecifica = null;
let horaFinalEspecifica = null;

// Extraer par√°metros de la solicitud si est√°n disponibles
const parametros = datos?.parametros || {};

if (parametros.fecha) {
  // Convertir la fecha espec√≠fica a Date
  fechaEspecifica = new Date(parametros.fecha);

  // Verificar si la fecha est√° dentro del l√≠mite de 2 meses
  const ahora = new Date();
  const limiteFecha = new Date();
  limiteFecha.setMonth(limiteFecha.getMonth() + LIMITE_MESES);

  if (fechaEspecifica > limiteFecha) {
    return JSON.stringify({
      success: false,
      error: `Lo siento, no puedo programar citas m√°s all√° de ${LIMITE_MESES} meses desde hoy. Por favor, intenta con una fecha m√°s cercana.`
    });
  }
}

if (parametros.hora) {
  // Convertir la hora espec√≠fica a n√∫mero decimal (por ejemplo, "15:30" -> 15.5)
  const [horas, minutos] = parametros.hora.split(':').map(Number);
  horaEspecifica = horas + (minutos || 0) / 60;

  // IMPORTANTE: Interpretar la hora como "a partir de este momento"
  // No establecemos horaFinalEspecifica aqu√≠ para mostrar todos los slots desde horaEspecifica en adelante
}

// Si se especifica un rango de horas
if (parametros.horaFin) {
  const [horasFin, minutosFin] = parametros.horaFin.split(':').map(Number);
  horaFinalEspecifica = horasFin + (minutosFin || 0) / 60;
}

// Extraer los eventos del objeto recibido
const items = datos?.items || datos?.body?.items || datos?.body || datos || [];
const eventos = Array.isArray(items) ? items : [];

// Funci√≥n para formatear hora en formato HH:MM
function formatTime(date) {
  return `${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}`;
}

// Funci√≥n para formatear fecha en YYYY-MM-DD
function formatDate(date) {
  return date.toISOString().split('T')[0];
}

// Convertir eventos a slots ocupados
const slotsOcupados = [];
for (const evento of eventos) {
  // Verificar que el evento tenga fecha y hora
  if (!evento.start || !evento.end) continue;

  const inicio = new Date(evento.start.dateTime || evento.start.date);
  const fin = new Date(evento.end.dateTime || evento.end.date);

  // Si es un evento de todo el d√≠a, ajustamos las horas
  if (evento.start.date && !evento.start.dateTime) {
    inicio.setHours(0, 0, 0, 0);
    fin.setHours(23, 59, 59, 999);
  }

  slotsOcupados.push({
    fecha: formatDate(inicio),
    inicio: formatTime(inicio),
    fin: formatTime(fin),
    inicioDate: inicio,
    finDate: fin
  });
}

// Obtener la fecha y hora actual
const ahora = new Date();
const horaActual = ahora.getHours();
const minutosActuales = ahora.getMinutes();

// Determinar la fecha de inicio basada en los par√°metros
const fechaInicio = fechaEspecifica ? new Date(fechaEspecifica) : new Date();

// Si no hay fecha espec√≠fica pero hay hora espec√≠fica para hoy
if (!fechaEspecifica && horaEspecifica !== null) {
  // Si la hora espec√≠fica ya pas√≥ hoy, empezamos desde ma√±ana
  if (horaEspecifica < horaActual + minutosActuales / 60) {
    fechaInicio.setDate(fechaInicio.getDate() + 1);
  }
}

// Generar rango de fechas a procesar
const fechaFin = new Date(fechaInicio);
fechaFin.setDate(fechaFin.getDate() + 14); // Dos semanas adelante para tener suficiente rango

// Generar todos los slots disponibles
const todosLosSlots = [];

// Para cada d√≠a en el rango
for (let fecha = new Date(fechaInicio); fecha < fechaFin; fecha.setDate(fecha.getDate() + 1)) {
  const diaSemana = fecha.getDay();

  // Verificar si es un d√≠a h√°bil
  if (!HORARIO_LABORAL.diasHabiles.includes(diaSemana)) {
    continue;
  }

  const fechaStr = formatDate(fecha);

  // Determinar hora de inicio para este d√≠a
  let horaInicioHoy = HORARIO_LABORAL.inicio;

  // Si hay una hora espec√≠fica solicitada, usarla como m√≠nimo
  // IMPORTANTE: Esta es la parte que cambia para interpretar "a las 12" como "a partir de las 12"
  if (horaEspecifica !== null) {
    horaInicioHoy = Math.max(horaInicioHoy, horaEspecifica);
  }

  // Verificar si el d√≠a es hoy
  const esHoy = fecha.getDate() === ahora.getDate() && 
                fecha.getMonth() === ahora.getMonth() && 
                fecha.getFullYear() === ahora.getFullYear();

  // Si es hoy y no hay fecha espec√≠fica, aplicar el margen de horas
  if (esHoy && !fechaEspecifica) {
    const tiempoMinimo = horaActual + MARGEN_HORAS + minutosActuales / 60;
    horaInicioHoy = Math.max(horaInicioHoy, Math.ceil(tiempoMinimo));
  }

  // Determinar hora de fin para este d√≠a
  let horaFinHoy = HORARIO_LABORAL.fin;

  // Si hay una hora final espec√≠fica, usarla como m√°ximo
  if (horaFinalEspecifica !== null) {
    horaFinHoy = Math.min(horaFinHoy, horaFinalEspecifica);
  }

  // Si la hora de inicio ya es despu√©s del fin del horario laboral, omitir este d√≠a
  if (horaInicioHoy >= horaFinHoy) {
    continue;
  }

  // Generar slots para este d√≠a
  const inicioJornada = new Date(fecha);
  inicioJornada.setHours(Math.floor(horaInicioHoy), (horaInicioHoy % 1) * 60, 0, 0);

  const finJornada = new Date(fecha);
  finJornada.setHours(Math.floor(horaFinHoy), (horaFinHoy % 1) * 60, 0, 0);

  // Crear slots de la duraci√≥n especificada
  for (let slot = new Date(inicioJornada); slot < finJornada; 
       slot.setMinutes(slot.getMinutes() + HORARIO_LABORAL.duracionCita)) {

    const slotInicio = new Date(slot);
    const slotFin = new Date(slot);
    slotFin.setMinutes(slotFin.getMinutes() + HORARIO_LABORAL.duracionCita);

    // Verificar si este slot llega hasta el final de la jornada
    if (slotFin > finJornada) {
      break;
    }

    // Verificar si el slot est√° disponible (no se solapa con ning√∫n evento ocupado)
    const estaDisponible = !slotsOcupados.some(ocupado => {
      // Solo verificar eventos del mismo d√≠a
      if (ocupado.fecha !== fechaStr) {
        return false;
      }

      // Hay solapamiento si:
      // (inicio del slot < fin del ocupado) Y (fin del slot > inicio del ocupado)
      return (slotInicio < ocupado.finDate && slotFin > ocupado.inicioDate);
    });

    // Si est√° disponible, a√±adirlo a la lista
    if (estaDisponible) {
      todosLosSlots.push({
        fecha: fechaStr,
        inicio: formatTime(slotInicio),
        fin: formatTime(slotFin),
        hora: `${formatTime(slotInicio)}-${formatTime(slotFin)}`,
        fechaCompleta: new Date(slotInicio)
      });
