// Este código convierte Markdown a DOCX utilizando bibliotecas externas
// Para n8n, se podría utilizar un nodo de ejecución de código personalizado con pandoc

const { exec } = require('child_process');
const fs = require('fs');
const path = require('path');
const os = require('os');

// Crear archivos temporales
const tempDir = os.tmpdir();
const markdownPath = path.join(tempDir, 'documento.md');
const docxPath = path.join(tempDir, 'documento.docx');

// Escribir el contenido markdown en el archivo temporal
fs.writeFileSync(markdownPath, items[0].json.choices[0].message.content);

// Ejecutar pandoc para convertir de markdown a docx
return new Promise((resolve, reject) => {
  exec(`pandoc ${markdownPath} -o ${docxPath}`, (error, stdout, stderr) => {
    if (error) {
      console.error(`Error al ejecutar pandoc: ${error}`);
      return reject(error);
    }

    // Leer el archivo DOCX generado
    const docxContent = fs.readFileSync(docxPath);

    // Crear un nuevo item con el contenido binario
    const newItem = {
      json: { ...items[0].json },
      binary: {
        data: {
          data: docxContent.toString('base64'),
          mimeType: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
          fileName: `documento_${items[0].json.datosDocumento.case_id}.docx`
        }
      }
    };

    // Limpiar archivos temporales
    fs.unlinkSync(markdownPath);
    fs.unlinkSync(docxPath);

    resolve([newItem]);
  });
});

