};

// Asegurar que tenemos un array de chunks
let chunks = [];
if (openAIResponse && openAIResponse.chunks && Array.isArray(openAIResponse.chunks)) {
  chunks = openAIResponse.chunks;
} else if (Array.isArray(openAIResponse)) {
  chunks = openAIResponse;
} else {
  // Si no encontramos el formato esperado, revisamos si hay otro formato
  const keys = Object.keys(openAIResponse);
  for (const key of keys) {
