// Parse the command output to get segment information
const commandOutput = $input.item.json.output;

// Find the JSON part at the end of the output
const jsonStart = commandOutput.lastIndexOf('{"totalSegments"');
const jsonData = commandOutput.substring(jsonStart);

// Parse the JSON data
const segmentData = JSON.parse(jsonData);

// Get tmp dir from the output
const tmpDirMatch = commandOutput.match(/Temporary directory: (.+?)\n/); 
const tmpDir = tmpDirMatch ? tmpDirMatch[1] : null;

// Create an item for each segment
const items = [];
for (const segment of segmentData.segmentFiles) {
  // Read the segment file
  const fs = require('fs');
  const segmentContent = fs.readFileSync(segment.path);

  // Create an item with the segment binary data
  items.push({
    json: {
      ....$input.item.json,
      segmentNumber: segment.segmentNumber,
      totalSegments: segmentData.totalSegments,
      tmpDir,
      needsSplitting: false // Mark as already split
    },
    binary: {
      data: {
        data: segmentContent.toString('base64'),
        mimeType: $input.item.binary.data.mimeType,
        fileName: `segment_${segment.segmentNumber}.${segment.path.split('.').pop()}`,
        fileSize: segment.size
      }
    }
  });
}

return items;

// Group transcriptions by original file
const items = [];

// Store our transcriptions for merging
const transcriptions = [];
let segmentNumber = 0;
let totalSegments = 0;
let originalFileInfo = null;

// Process transcriptions
for (const item of $input.all()) {
  if (item.json.originalFilename) {
    // Store original file info from any item
    originalFileInfo = {
      id: item.json.id,
      originalFilename: item.json.originalFilename,
      folderId: item.json.folderId,
      totalSegments: item.json.totalSegments || 1
    };
    totalSegments = item.json.totalSegments || 1;
  }

  // Add this segment's transcription
  transcriptions.push({
    segmentNumber: item.json.segmentNumber || 1,
    text: item.json.text || ''
  });

  // Clean up temporary files if this was the last segment
  if (item.json.tmpDir && item.json.segmentNumber === item.json.totalSegments) {
    // In a production environment, you would add code here to 
    // delete the temporary directory using fs.rmSync(item.json.tmpDir, { recursive: true })
    // But for safety, we'll just log it for now
    console.log(`Would clean up temporary directory: ${item.json.tmpDir}`);
  }
}

// Sort transcriptions by segment number
transcriptions.sort((a, b) => a.segmentNumber - b.segmentNumber);

// Combine all transcriptions
const fullTranscription = transcriptions.map(t => t.text).join('\n\n');

// Return a single item with the combined transcription
return [
  {
    json: {
      ...originalFileInfo,
      text: fullTranscription,
      transcriptionComplete: true
    }
