    totalFreeSlots: freeSlots.length,
    slotsReturned: freeSlots.length,
    freeSlots: freeSlots,
    formattedText: errors.length > 0 ? 
      "No se pudieron determinar los slots libres debido a errores en el procesamiento. Por favor, revisa la configuración." : 
      formatSlotsAsNaturalText(freeSlots)
  }
};

// Configuración de horarios de trabajo
const HORARIO_LABORAL = {
  inicio: 9, // 9:00 AM
  fin: 18,  // 6:00 PM
  duracionCita: 60, // minutos (1 hora)
  diasHabiles: [1, 2, 3, 4, 5] // Lunes a Viernes (0=Domingo, 6=Sábado)
};

// Configuración de margen de tiempo y límites
const MARGEN_HORAS = 4; // Margen de 4 horas para citas más inmediatas
