// Código para determinar normativas aplicables
const proyectoData = $input.first().json.data;
const proyecto = JSON.parse(proyectoData);
const normativasData = $input.item(1).json.data;
const normativas = JSON.parse(normativasData);

// Extraer datos relevantes del proyecto
const tipoProyecto = proyecto.tipo_proyecto;
const ubicacion = {
  comunidadAutonoma: proyecto.comunidad_autonoma,
  municipio: proyecto.municipio,
  provincia: proyecto.provincia
};
const caracteristicas = proyecto.caracteristicas || {};

// Filtrar normativas aplicables según tipo de proyecto y ubicación
const normativasAplicables = normativas.filter(normativa => {
  // Verificar si la normativa aplica al tipo de proyecto
  const aplicaTipoProyecto = normativa.tipos_proyecto.includes(tipoProyecto);

  // Verificar si la normativa aplica a la ubicación
  const aplicaUbicacion = 
    (normativa.ambito === 'nacional') || 
    (normativa.ambito === 'autonomico' && normativa.comunidad_autonoma === ubicacion.comunidadAutonoma) ||
    (normativa.ambito === 'provincial' && normativa.provincia === ubicacion.provincia) ||
    (normativa.ambito === 'municipal' && normativa.municipio === ubicacion.municipio);

  // Verificar si la normativa aplica según las características específicas
  let aplicaCaracteristicas = true;
  if (normativa.caracteristicas_requeridas) {
    for (const [clave, valor] of Object.entries(normativa.caracteristicas_requeridas)) {
      if (caracteristicas[clave] !== valor) {
        aplicaCaracteristicas = false;
        break;
      }
    }
  }

  return aplicaTipoProyecto && aplicaUbicacion && aplicaCaracteristicas;
});

// Extraer documentos obligatorios de las normativas aplicables con reglas de validación
const documentosObligatorios = [];
normativasAplicables.forEach(normativa => {
  normativa.documentos_requeridos.forEach(documento => {
    if (documento.obligatorio) {
      // Evitar duplicados
      if (!documentosObligatorios.some(doc => doc.id === documento.id)) {
        documentosObligatorios.push({
          id: documento.id,
          nombre: documento.nombre,
          descripcion: documento.descripcion,
          normativa: normativa.nombre,
          referencia: normativa.referencia,
          patrones_nombre: documento.patrones_nombre || [],
          reglas_validacion: documento.reglas_validacion || [],
          parametros_especificos: documento.parametros_especificos || {},
          texto_obligatorio: documento.texto_obligatorio || []
        });
      }
    }
  });
});

return {
  json: {
    proyectoId: proyecto.id,
    nombreProyecto: proyecto.nombre,
    tipoProyecto: tipoProyecto,
    ubicacion: ubicacion,
    caracteristicas: caracteristicas,
    superficieTotal: proyecto.superficie_total || 0,
    plantas: proyecto.plantas || 0,
    usoEdificio: proyecto.uso_edificio || '',
    normativasAplicables: normativasAplicables.map(n => n.nombre),
    documentosObligatorios: documentosObligatorios,
    cantidadDocumentosObligatorios: documentosObligatorios.length
  }
};

// Código para identificar documentos existentes
const datosNormativas = $input.first().json;
const documentosExistentes = $input.last().json;

// Documentos obligatorios según normativa
const documentosObligatorios = datosNormativas.documentosObligatorios;

// Identificar cada documento obligatorio y preparar para análisis de contenido
const documentosParaAnalisis = [];

documentosObligatorios.forEach(docObligatorio => {
  // Buscar si existe algún documento que coincida con los patrones de nombre
  const documentosCoincidentes = documentosExistentes.filter(docExistente => {
    const nombreArchivo = docExistente.name.toLowerCase();

    // Verificar si coincide con alguno de los patrones
    return docObligatorio.patrones_nombre.some(patron => {
      // Convertir el patrón a expresión regular
      const regexPatron = new RegExp(patron.toLowerCase());
      return regexPatron.test(nombreArchivo);
    });
  });

  // Información sobre el documento y su estado
  const documentoInfo = {
    id: docObligatorio.id,
    nombre: docObligatorio.nombre,
    normativa: docObligatorio.normativa,
    referencia: docObligatorio.referencia,
    encontrado: documentosCoincidentes.length > 0,
    documentosCoincidentes: documentosCoincidentes.map(d => ({
      nombre: d.name,
      path: d.path,
      extension: d.name.split('.').pop().toLowerCase(),
      tamano: d.size || 0
    })),
    reglas_validacion: docObligatorio.reglas_validacion || [],
    parametros_especificos: docObligatorio.parametros_especificos || {},
    texto_obligatorio: docObligatorio.texto_obligatorio || []
  };

  documentosParaAnalisis.push(documentoInfo);
});

// Calcular estadísticas iniciales
const documentosFaltantes = documentosParaAnalisis.filter(d => !d.encontrado);
const documentosEncontrados = documentosParaAnalisis.filter(d => d.encontrado);

return {
  json: {
    proyecto: datosNormativas,
    documentosParaAnalisis: documentosParaAnalisis,
    totalDocumentosObligatorios: documentosParaAnalisis.length,
    documentosEncontrados: documentosEncontrados.length,
    documentosFaltantes: documentosFaltantes.length,
    porcentajeExistencia: Math.round((documentosEncontrados.length / documentosParaAnalisis.length) * 100)
  }
};

