// Extraer documentos para analisis
const datos = $input.first().json;
const documentoActual = datos.documentosParaAnalisis[$input.first().context.currentRunIndex || 0];

// Si no hay documento coincidente, no procesamos
if (!documentoActual.encontrado || !documentoActual.documentosCoincidentes.length) {
  return {
    json: {
      documento: documentoActual,
      analizable: false,
      mensaje: 'Documento no encontrado para analisis'
    }
  };
}

// Seleccionamos el primer documento coincidente para analisis
const docAAnalizar = documentoActual.documentosCoincidentes[0];

// Determinamos el tipo de procesamiento segun la extension
let tipoAnalisis = 'desconocido';
let extractor = 'ninguno';

switch (docAAnalizar.extension) {
  case 'pdf':
    tipoAnalisis = 'documento_texto';
    extractor = 'pdf_text';
    break;
  case 'docx':
  case 'doc':
    tipoAnalisis = 'documento_texto';
    extractor = 'docx_text';
    break;
  case 'txt':
    tipoAnalisis = 'documento_texto';
    extractor = 'plain_text';
    break;
  case 'dwg':
  case 'dxf':
    tipoAnalisis = 'plano_cad';
    extractor = 'cad_metadata';
    break;
  case 'jpg':
  case 'png':
  case 'tif':
    tipoAnalisis = 'imagen';
    extractor = 'ocr';
    break;
  case 'xlsx':
  case 'xls':
    tipoAnalisis = 'hoja_calculo';
    extractor = 'excel_data';
    break;
  default:
    tipoAnalisis = 'binario';
    extractor = 'metadata_only';
}

// Preparamos la informacion para el siguiente paso
return {
  json: {
    documento: documentoActual,
    docAAnalizar: docAAnalizar,
    tipoAnalisis: tipoAnalisis,
    extractor: extractor,
    analizable: true,
    rutaCompleta: docAAnalizar.path
  }
};

