// Validacion de contenido para documentos de texto
const datos = $input.first().json;
const documento = datos.documento;
const contenidoTexto = datos.contenidoTexto || '';

// Inicializamos resultados
const resultados = {
  documento_id: documento.id,
  documento_nombre: documento.nombre,
  reglas_aplicadas: documento.reglas_validacion || [],
  validaciones: [],
  cumplimiento_total: true,
  puntuacion: 100
};

// Validaciones especificas segun las reglas definidas
documento.reglas_validacion.forEach(regla => {
  let validacion = {
    regla: regla,
    cumple: false,
    detalles: '',
    gravedad: 'alta'  // por defecto
  };

  switch (regla) {
    case 'contenido_obligatorio':
      // Verificar si contiene todas las secciones obligatorias
      if (documento.parametros_especificos && documento.parametros_especificos.seccion_obligatoria) {
        const seccionesObligatorias = documento.parametros_especificos.seccion_obligatoria;
        const seccionesFaltantes = [];

        seccionesObligatorias.forEach(seccion => {
          // Busqueda simple de la seccion en el texto
          if (!contenidoTexto.includes(seccion)) {
            seccionesFaltantes.push(seccion);
          }
        });

        if (seccionesFaltantes.length === 0) {
          validacion.cumple = true;
          validacion.detalles = 'El documento contiene todas las secciones obligatorias requeridas';
        } else {
          validacion.cumple = false;
          validacion.detalles = `Faltan las siguientes secciones obligatorias: ${seccionesFaltantes.join(', ')}`;
        }
      } else {
        validacion.cumple = false;
        validacion.detalles = 'No se han definido secciones obligatorias para este documento';
      }
      break;

    case 'referencias_normativas':
      // Verificar si menciona textos obligatorios (referencias a normativas)
      if (documento.texto_obligatorio && documento.texto_obligatorio.length > 0) {
        const textosFaltantes = [];

        documento.texto_obligatorio.forEach(texto => {
          const textoLower = texto.toLowerCase();
          const contenidoLower = contenidoTexto.toLowerCase();

          if (!contenidoLower.includes(textoLower)) {
            textosFaltantes.push(texto);
          }
        });

        if (textosFaltantes.length === 0) {
          validacion.cumple = true;
          validacion.detalles = 'El documento incluye todas las referencias normativas obligatorias';
        } else {
          validacion.cumple = false;
          validacion.detalles = `Faltan las siguientes referencias normativas: ${textosFaltantes.join(', ')}`;
        }
      } else {
        validacion.cumple = true;
        validacion.detalles = 'No hay textos obligatorios definidos para este documento';
      }
      break;

    // Otras reglas de validacion podrian anadirse aqui...

    default:
      validacion.cumple = false;
      validacion.detalles = `Regla de validacion '${regla}' no implementada`;
      validacion.gravedad = 'baja';  // Si no reconocemos la regla, bajamos la gravedad
  }

  // Anadir esta validacion a los resultados
  resultados.validaciones.push(validacion);

  // Si alguna validacion falla, el cumplimiento total es falso
  if (!validacion.cumple && validacion.gravedad === 'alta') {
    resultados.cumplimiento_total = false;
  }
});

// Calculo de puntuacion basado en validaciones (ponderado por gravedad)
let validacionesImportantes = 0;
let validacionesCumplidas = 0;

resultados.validaciones.forEach(val => {
  if (val.gravedad === 'alta') {
    validacionesImportantes++;
    if (val.cumple) validacionesCumplidas++;
  }
});

if (validacionesImportantes > 0) {
  resultados.puntuacion = Math.round((validacionesCumplidas / validacionesImportantes) * 100);
}

return {
  json: {
    ...datos,
    resultados_validacion: resultados
  }
};

// Validacion para planos CAD
const datos = $input.first().json;
const documento = datos.documento;
const metadatosCAD = datos.metadatosCAD || {};

// Inicializamos resultados
const resultados = {
  documento_id: documento.id,
  documento_nombre: documento.nombre,
  reglas_aplicadas: documento.reglas_validacion || [],
  validaciones: [],
  cumplimiento_total: true,
  puntuacion: 100
};

// Validaciones especificas segun las reglas definidas para planos
documento.reglas_validacion.forEach(regla => {
  let validacion = {
    regla: regla,
    cumple: false,
    detalles: '',
    gravedad: 'alta'  // por defecto
  };

  switch (regla) {
    case 'revision_escala':
      // Verificar si la escala del plano es una de las permitidas
      if (documento.parametros_especificos && documento.parametros_especificos.escalas_permitidas) {
        const escalasPermitidas = documento.parametros_especificos.escalas_permitidas;
        const escalaActual = metadatosCAD.escala || 'desconocida';

        if (escalasPermitidas.includes(escalaActual)) {
          validacion.cumple = true;
          validacion.detalles = `La escala ${escalaActual} cumple con las escalas permitidas`;
        } else {
          validacion.cumple = false;
          validacion.detalles = `La escala ${escalaActual} no esta entre las permitidas: ${escalasPermitidas.join(', ')}`;
        }
      } else {
        validacion.cumple = false;
        validacion.detalles = 'No se han definido escalas permitidas para este plano';
      }
      break;

    case 'medidas_minimas':
      // Verificar si los espacios cumplen con las dimensiones minimas
      if (metadatosCAD.espacios && metadatosCAD.espacios.length > 0) {
        const espaciosIncumplimiento = [];

        // Valores minimos simulados (en un caso real vendrian de la normativa)
        const minimos = {
          'Salon': 18,  // m2
          'Dormitorio 1': 10,
          'Cocina': 7,
          'Bano': 4,
          'Pasillo': 0 // sin minimo
        };

        metadatosCAD.espacios.forEach(espacio => {
          const minimoRequerido = minimos[espacio.nombre] || 0;

          if (espacio.area < minimoRequerido) {
            espaciosIncumplimiento.push(`${espacio.nombre} (${espacio.area} m2 < ${minimoRequerido} m2)`);
          }
        });

        if (espaciosIncumplimiento.length === 0) {
          validacion.cumple = true;
          validacion.detalles = 'Todos los espacios cumplen con las dimensiones minimas';
        } else {
          validacion.cumple = false;
          validacion.detalles = `Los siguientes espacios no cumplen con las dimensiones minimas: ${espaciosIncumplimiento.join(', ')}`;
        }
      } else {
        validacion.cumple = false;
        validacion.detalles = 'No se han podido detectar espacios en el plano para validar dimensiones';
      }
      break;

    // Otras reglas de validacion podrian anadirse aqui...

    default:
      validacion.cumple = false;
      validacion.detalles = `Regla de validacion '${regla}' no implementada para planos`;
      validacion.gravedad = 'baja';  // Si no reconocemos la regla, bajamos la gravedad
  }

  // Anadir esta validacion a los resultados
  resultados.validaciones.push(validacion);

  // Si alguna validacion falla, el cumplimiento total es falso
  if (!validacion.cumple && validacion.gravedad === 'alta') {
    resultados.cumplimiento_total = false;
  }
});

// Calculo de puntuacion basado en validaciones
let validacionesImportantes = 0;
let validacionesCumplidas = 0;

resultados.validaciones.forEach(val => {
  if (val.gravedad === 'alta') {
    validacionesImportantes++;
    if (val.cumple) validacionesCumplidas++;
  }
});

if (validacionesImportantes > 0) {
  resultados.puntuacion = Math.round((validacionesCumplidas / validacionesImportantes) * 100);
}

return {
  json: {
    ...datos,
    resultados_validacion: resultados
  }
};

// Validacion para otros tipos de documentos
const datos = $input.first().json;
const documento = datos.documento;

// Inicializamos resultados genericos para otros tipos de documentos
const resultados = {
  documento_id: documento.id,
  documento_nombre: documento.nombre,
  reglas_aplicadas: documento.reglas_validacion || [],
  validaciones: [
    {
      regla: 'validacion_basica',
      cumple: true,
      detalles: 'Validacion basica de existencia del documento',
      gravedad: 'alta'
    }
  ],
  cumplimiento_total: true,
  puntuacion: 100
};

// En un sistema real, aqui se implementarian validaciones especificas
// para otros tipos de documentos como presupuestos, certificados, etc.

return {
  json: {
    ...datos,
    resultados_validacion: resultados
  }
};

