parameters.functionCode:
// Calculo del resultado global de la validacion
const datos = $input.first().json;
const resultadosDocumentos = datos.resultados_documentos || [];

// Estadisticas globales
let documentosTotales = resultadosDocumentos.length;
let documentosCumplen = resultadosDocumentos.filter(doc => doc.cumplimiento).length;
let puntuacionMedia = 0;

if (documentosTotales > 0) {
  puntuacionMedia = Math.round(
    resultadosDocumentos.reduce((sum, doc) => sum + doc.puntuacion, 0) / documentosTotales
  );
}

// Listado de problemas encontrados por normativa
const problemasPorNormativa = {};

resultadosDocumentos.forEach(doc => {
  // Solo procesamos documentos que no cumplen
  if (!doc.cumplimiento) {
    const norma = doc.normativa;

    if (!problemasPorNormativa[norma]) {
      problemasPorNormativa[norma] = [];
    }

    // Buscamos los detalles de los incumplimientos
    doc.detalles.forEach(detalle => {
      if (!detalle.cumple) {
        problemasPorNormativa[norma].push({
          documento: doc.documento_nombre,
          regla: detalle.regla,
          descripcion: detalle.detalles
        });
      }
    });
  }
});

// Resultado final
const cumplimientoTotal = documentosCumplen === documentosTotales;
const porcentajeCumplimiento = Math.round((documentosCumplen / documentosTotales) * 100);

return {
  json: {
    proyecto: datos.proyecto,
    resultados_documentos: resultadosDocumentos,
    resultado_global: {
      documentosTotales,
      documentosCumplen,
      documentosNoCumplen: documentosTotales - documentosCumplen,
      porcentajeCumplimiento,
      puntuacionMedia,
      cumplimientoTotal,
      estado: cumplimientoTotal ? 'CONFORME' : 'NO CONFORME',
      problemasPorNormativa,
      fechaValidacion: new Date().toISOString()
    }
  }
};

// Generacion de informe HTML para proyectos CONFORMES
const datos = $input.first().json;
const plantillaHTML = $input.last().json.data;

// Formatear fecha
const fechaValidacion = new Date(datos.resultado_global.fechaValidacion).toLocaleDateString('es-ES', {
  day: '2-digit',
  month: 'long',
  year: 'numeric'
});

// Generar tabla de resultados por documento
let tablaDocumentos = '';
datos.resultados_documentos.forEach(doc => {
  tablaDocumentos += `
    <tr class="${doc.cumplimiento ? 'cumple' : 'no-cumple'}">
      <td>${doc.documento_nombre}</td>
      <td>${doc.normativa}</td>
      <td>${doc.puntuacion}%</td>
      <td class="estado">${doc.cumplimiento ? 'CUMPLE' : 'NO CUMPLE'}</td>
    </tr>
  `;
});

// Reemplazar variables en la plantilla
let informeHTML = plantillaHTML
  .replace('{{nombreProyecto}}', datos.proyecto.nombreProyecto)
  .replace('{{proyectoId}}', datos.proyecto.proyectoId)
  .replace('{{fechaValidacion}}', fechaValidacion)
  .replace('{{tipoProyecto}}', datos.proyecto.tipoProyecto)
  .replace('{{ubicacion}}', `${datos.proyecto.ubicacion.municipio}, ${datos.proyecto.ubicacion.provincia}, ${datos.proyecto.ubicacion.comunidadAutonoma}`)
  .replace('{{porcentajeCumplimiento}}', datos.resultado_global.porcentajeCumplimiento)
  .replace('{{puntuacionMedia}}', datos.resultado_global.puntuacionMedia)
  .replace('{{tablaDocumentos}}', tablaDocumentos)
  .replace('{{totalDocumentos}}', datos.resultado_global.documentosTotales)
  .replace('{{documentosConformes}}', datos.resultado_global.documentosCumplen);

// Generar nombre de archivo para el informe
const nombreArchivo = `informe_validacion_${datos.proyecto.proyectoId}_${new Date().toISOString().split('T')[0]}.html`;

return {
  json: {
    ...datos,
    nombreInforme: nombreArchivo,
    estadoProyecto: 'CONFORME'
  },
  binary: {
    informe: {
      data: Buffer.from(informeHTML).toString('base64'),
      mimeType: 'text/html',
      fileName: nombreArchivo
    }
  }
};

// Generacion de informe HTML para proyectos NO CONFORMES
const datos = $input.first().json;
const plantillaHTML = $input.last().json.data;

// Formatear fecha
const fechaValidacion = new Date(datos.resultado_global.fechaValidacion).toLocaleDateString('es-ES', {
  day: '2-digit',
  month: 'long',
  year: 'numeric'
});

// Generar tabla de resultados por documento
let tablaDocumentos = '';
datos.resultados_documentos.forEach(doc => {
  tablaDocumentos += `
    <tr class="${doc.cumplimiento ? 'cumple' : 'no-cumple'}">
      <td>${doc.documento_nombre}</td>
      <td>${doc.normativa}</td>
      <td>${doc.puntuacion}%</td>
      <td class="estado">${doc.cumplimiento ? 'CUMPLE' : 'NO CUMPLE'}</td>
    </tr>
  `;
});

// Generar lista de problemas por normativa
let listaProblemas = '';
const problemasNormativa = datos.resultado_global.problemasPorNormativa;

for (const [normativa, problemas] of Object.entries(problemasNormativa)) {
  listaProblemas += `
    <div class="problemas-normativa">
      <h4>Normativa: ${normativa}</h4>
      <ul>
  `;

  problemas.forEach(problema => {
    listaProblemas += `
        <li>
          <strong>${problema.documento}</strong> - ${problema.descripcion}
        </li>
    `;
  });

  listaProblemas += `
      </ul>
    </div>
  `;
}

// Reemplazar variables en la plantilla
let informeHTML = plantillaHTML
  .replace('{{nombreProyecto}}', datos.proyecto.nombreProyecto)
  .replace('{{proyectoId}}', datos.proyecto.proyectoId)
  .replace('{{fechaValidacion}}', fechaValidacion)
  .replace('{{tipoProyecto}}', datos.proyecto.tipoProyecto)
  .replace('{{ubicacion}}', `${datos.proyecto.ubicacion.municipio}, ${datos.proyecto.ubicacion.provincia}, ${datos.proyecto.ubicacion.comunidadAutonoma}`)
  .replace('{{porcentajeCumplimiento}}', datos.resultado_global.porcentajeCumplimiento)
  .replace('{{puntuacionMedia}}', datos.resultado_global.puntuacionMedia)
  .replace('{{tablaDocumentos}}', tablaDocumentos)
  .replace('{{totalDocumentos}}', datos.resultado_global.documentosTotales)
  .replace('{{documentosNoConformes}}', datos.resultado_global.documentosNoCumplen)
  .replace('{{listaProblemas}}', listaProblemas);

// Generar nombre de archivo para el informe
const nombreArchivo = `informe_validacion_${datos.proyecto.proyectoId}_${new Date().toISOString().split('T')[0]}.html`;

return {
  json: {
    ...datos,
    nombreInforme: nombreArchivo,
    estadoProyecto: 'NO CONFORME'
  },
  binary: {
    informe: {
      data: Buffer.from(informeHTML).toString('base64'),
      mimeType: 'text/html',
      fileName: nombreArchivo
    }
  }
};

